  private _languageConvertion(str: string, dict: { [key: string]: string }) {
    var n_str = [];

    for (var i = 0; i < str.length; ++i) {
      n_str.push(
        dict[str[i]]
        || dict[str[i].toLowerCase()] == undefined && str[i]
        || dict[str[i].toLowerCase()].toUpperCase()
      );
    }

    return n_str.join('');
  }

  private _bukvitsaConvertion(str: string, dict: { [key: string]: Bukvitsa[] }): Bukvitsa[] {
    var bukvitsaWord: Bukvitsa[] = [];

    for (var i = 0; i < str.length; ++i) {
      if (str[i] !== '_') {
        bukvitsaWord.push(...dict[str[i]]);
        continue;
      }

      let ii = i + 1;
      let subArr = [str[i]];

      while (str[ii] != '_')
        subArr.push(str[ii]);

      const resBukvitsa = dict[subArr.join('')];
      bukvitsaWord.push(...resBukvitsa);
    }

    return bukvitsaWord;
  }
